public class Assignment0825 {
    public static void main(String[] args) {
        String arr = "`~!@#$%^&*()-_=+[]{}\\|;:'\",.<>/?";

        int maxLength = 0;
        tire1:
        for (int i = 0; i < arr.length(); i++) {
            tire2:
            for (int j = i+1 ; j < arr.length(); j++) {
                String beforeCheck = arr.substring(i,j );
                //tire3/4用于检查循环中的数是否有相同字符
                //相同直接continue tire2
                tire3:
                for (int o = 0; o < beforeCheck.length(); o++) {
                    tire4:
                    for (int k = o + 1; k < beforeCheck.length(); k++) {
                        if (beforeCheck.charAt(o) == beforeCheck.charAt(k)) continue tire2;//循环检查
                    }
                }
                //String[] afterSelfCheck = arr.split(beforeCheck);
                if (beforeCheck.length() > maxLength) maxLength = beforeCheck.length();


            }
        }
        System.out.println(maxLength);

    }


}
